import { useState, useEffect } from "react";
import { createQuestion, updateQuestion, readQuestion } from "../api/api";
import "../styles/questionForm.css";
export default function QuestionForm({ question_id, formType }) {
  const initialFormState = {
    question: "",
    answer_a: "",
    answer_b: "",
    answer_c: "",
    answer_d: "",
    correct: "",
  };
  const [formData, setFormData] = useState({ ...initialFormState });

  useEffect(()=>{
    async function readQuestionInfo(){
      if(!question_id){
        return;
      }
      const ac = new AbortController();
      try{
        const questionInformation = await readQuestion(question_id, ac.signal)
        if(questionInformation){
          setFormData(questionInformation.data)
        }
      }catch(error){
        if(error.name === "AbortError"){
          console.log("Aborted")
          ac.abort();
        }else{
          throw error;
        }
      }
      return () => ac.abort();
    }
    readQuestionInfo();
  }, [question_id]);

  const handleChange = (e) => {
    let stateValue = e.target.value;
    setFormData({ ...formData, [e.target.name]: stateValue });
    console.log("this is formdata", formData)
  };

  const handleSubmit = (e) => {
    const ac = new AbortController();
    e.preventDefault();
    async function addQuestionToQuiz() {
      try {
        if (formType === "create") {
          await createFunction();
        } else if (formType === "edit") {
          await editFunction();
          console.log("this is formdata after waiting for edit function", formData)
        }
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("Aborted");
        } else {
          throw error;
        }
      }
    }

    addQuestionToQuiz();
    return () => ac.abort();
  };

  const createFunction = async () => {
    const ac = new AbortController();
    try {
      await createQuestion(formData, ac.signal);
    } catch (error) {
      if (error.name === "AbortError") {
        console.log("Aborted");
      } else {
        throw error;
      }
    }
    return () => ac.abort();
  };

  const editFunction = async () => {
    const ac = new AbortController();
    try {
      console.log("this is formdata before posting update", formData)
      await updateQuestion(formData, ac.signal);
    } catch (error) {
      if (error.name === "AbortError") {
        console.log("Aborted!");
      } else {
        throw error;
      }
    }
    return ac.abort();
  };

  return (
    <div>
      
    </div>
  );
}
